queries.query:
PREFIX ns1: <https://enpkg.commons-lab.org/kg/>
PREFIX ns2: <https://enpkg.commons-lab.org/module/>
SELECT ?InChIkey
WHERE {
    ?chemical ns2:has_chembl_activity ?assayResult .
    ?assayResult ns2:target_id ChEMBLTarget ;
                 ns2:activity_type "type" ;
                 ns2:activity_value ?activityValue .
    FILTER(?activityValue < value)
    ?InChIkey ns2:has_chembl_id ?chemical .
}
